let params = queryString.split('&');

// Végigmegyünk a paramétereken, és megkeressük a "redirect_uri" értékét
let redirectUri = null;
let state = null;
let clientid = null;
for (let param of params) {
    let [key, value] = param.split('=');
    if (key === 'redirect_uri') {
        redirectUri = decodeURIComponent(value);  // Dekódoljuk az URI-t
    }
    if (key === 'state') {
        state = decodeURIComponent(value);  // Dekódoljuk az URI-t
    }
    if (key === 'client_id') {
        clientid = decodeURIComponent(value);  // Dekódoljuk az URI-t
    }
}

